import React, { useState } from "react";

import "./AppExpenses.css";
import "./ExpenseItem.css";

import ExpensesFilter from "../NewExpense/ExpensesFilter";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";

function AppExpenses(props) {
  const [savedYear, setSavedYear] = useState("2020");
  const yearChangeHAndler = (year) => {
    setSavedYear(year);
  };

  const yearFilter = props.items.filter((array) => {
    return array.date.getFullYear === savedYear;
  });

  return (
    <Card className="expenses">
      <ExpensesFilter
        selectedYEar={savedYear}
        onSaveYear={yearChangeHAndler}
      ></ExpensesFilter>
      {yearFilter.map((e) => (
        <ExpenseItem
          title={e.title}
          amount={e.amount}
          date={e.date}
          key={e.id}
        ></ExpenseItem>
      ))}
    </Card>
  );
}
export default AppExpenses;
