import React, { useState } from "react";

import "./AppExpenses.css";
import "./ExpenseItem.css";

import ExpensesFilter from "../NewExpense/ExpensesFilter";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";

function AppExpenses(props) {
  const [savedYear, setSavedYear] = useState("2020");
  const yearChangeHAndler = (year) => {
    setSavedYear(year);
  };

  return (
    <Card className="expenses">
      <ExpensesFilter
        selectedYEar={savedYear}
        onSaveYear={yearChangeHAndler}
      ></ExpensesFilter>
      {props.items.map((e) => (
        <ExpenseItem title = {e.title} amount = {e.amount} date = {e.date}></ExpenseItem>
      ))}
      <ExpenseItem
        title={props.items[0].title}
        amount={props.items[0].amount}
        date={props.items[0].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[1].title}
        amount={props.items[1].amount}
        date={props.items[1].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[2].title}
        amount={props.items[2].amount}
        date={props.items[2].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[3].title}
        amount={props.items[3].amount}
        date={props.items[3].date}
      ></ExpenseItem>
    </Card>
  );
}
export default AppExpenses;
