import React, { useState } from "react";

import "./AppExpenses.css";
import "./ExpenseItem.css";

import ExpensesFilter from "../NewExpense/ExpensesFilter";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";

function AppExpenses(props) {

  const [savedYear, setSavedYear] = useState("2020");
  const yearChangeHAndler = (year) => {
    setSavedYear(year);
  };


  const yearFilter = props.items.filter((array) => {
    return array.date.getFullYear().toString() === savedYear;
  });


  if(yearFilter.length === 0){
    expenseContent = <p style={{ color: "white" }}>There is no elements found</p>
  }
  return (
    <Card className="expenses">
      <ExpensesFilter
        selectedYEar={savedYear}
        onSaveYear={yearChangeHAndler}
      ></ExpensesFilter>
      {/* {yearFilter.length === 0 ? (
        expenseContent
      ) : (
        yearFilter.map((e) => (
          <ExpenseItem
            title={e.title}               //second way. Writing more code, but a way simpler in understanding
            amount={e.amount}
            date={e.date}
            key={e.id}
          ></ExpenseItem>
        ))
      )} */}

{yearFilter.length === 0 && (
        expenseContent
      )}
      {yearFilter.length > 0 &&     // awesome way of doing magic with &&
        yearFilter.map((e) => (
          <ExpenseItem
            title={e.title}
            amount={e.amount}
            date={e.date}
            key={e.id}
          ></ExpenseItem>
        ))}
    </Card>
  );
}
export default AppExpenses;
