import './ExpenseItem.css'
function ExpenseItem(props) {
  const month = props.date.toLocaleString('en-US', {year: 'numeric', month: 'long', day: 'numeric'})
  const year = props.date.toLocaleString('en-US', {year: 'numeric', month: 'long', day: 'numeric'})
  return (
    <div className = 'expense-item'>
      <div>
        <div>{month}</div>
        <div></div>
        <div></div>
          </div>
      <div className = 'expense-item__description'>
        <h2>{props.title}</h2>
        <div className = 'expense-item__price'>${props.amount}</div>
      </div>
    </div>
  );
}
export default ExpenseItem;
